import React, { Component } from 'react';
import Pelicula from './Pelicula';
import Slider from './Slider';
import Sidebar from './Sidebar';

class Peliculas extends Component {

    state = {
        peliculas: [
            {
                titulo: 'Batman vs Superman',
                image: 'data:image/jpeg;base64,'
            },
            {
                titulo: 'Avenger',
                image: 'data:image/jpeg;base64,'
            },

        ],
        nombre: 'Luis A Aguilar',
        favorita: {}
    }

    cambiarTitulo = () => {
        var { peliculas } = this.state;
        peliculas[0].titulo = 'SUPERMAN vs BATMAN'

        this.setState({
            peliculas: peliculas
        })
    }

    favorita = (pelicula, indice) => {
        // console.log("MARCADA");
        // console.log(pelicula, indice);
        this.setState({
            favorita: pelicula
        })
    }


    render() {

        var pStyle = {
            background: 'green',
            color: 'white',
            padding: '10px'
        }

        var favorita;
        if (this.state.favorita.titulo) {
            favorita = (
                <p className='favorita' style={pStyle}>
                    <strong>La película favorita es: </strong>
                    <span> {this.state.favorita.titulo} </span>
                </p>
            );
        } else {
            favorita = (
                <p>
                    <strong>No hay película favorita aun </strong>
                </p>
            )
        }

        return (
            <React.Fragment>
                <Slider
                    title="Películas"
                    size="slider-small"

                />
                <div className="center">
                    <div id="content" className="peliculas">
                        <h2 className="subheader">Listado de  Peliculas</h2>
                        <p>Selección de peliculas favoritas de: {this.state.nombre}</p>
                        <p>
                            <button onClick={this.cambiarTitulo}>Cambiar Nombre</button>
                        </p>
                        {/* {this.state.favorita.titulo &&
                            <p>
                                <strong>La película favorita es: </strong>
                                <span> {this.state.favorita.titulo} </span>
                            </p>
                        } */}

                        {/* {this.state.favorita.titulo ? (
                            <p className='favorita' style={pStyle}>
                                <strong>La película favorita es: </strong>
                                <span> {this.state.favorita.titulo} </span>
                            </p>
                            ) : (
                                <p>
                                    <strong>No hay película favorita aun </strong>
                                </p>
                            )

                            } */
                        favorita
                        }

                        <div id="articles" className="peliculas">
                            {
                                this.state.peliculas.map((pelicula, i) => {
                                    return (
                                        <Pelicula
                                            key={i}
                                            pelicula={pelicula}
                                            indice={i}
                                            marcarFavorita={this.favorita}
                                        />
                                    );
                                })
                            }
                        </div>
                    </div>
                    <Sidebar
                        blog="true"
                    />
                </div>
            </React.Fragment>
        )
    }
}

export default Peliculas;